module.exports = function(RED) {
    function check_email_Node(config) {
        RED.nodes.createNode(this,config);
        var node = this;
        node.checkField = config.checkField
        node.checkFieldType = config.checkFieldType
        node.log(JSON.stringify(config));
        node.on('input', function(msg) {
                const emailRegex = /^[A-Za-z0-9](([_\.\-]?[a-zA-Z0-9]+)*)@([A-Za-z0-9]+)(([\.\-]?[a-zA-Z0-9]+)*)\.([A-Za-z]{2,})$/;
                if(emailRegex.test(String(msg.topic))){
                    node.send([msg,])
                }else{
                    node.send([,msg])
                }
                
            
        });
    }
    RED.nodes.registerType("check_email",check_email_Node);
}