module.exports = function(RED) {
    function check_fractions_Node(config) {
        RED.nodes.createNode(this,config);
        var node = this;
        node.checkField = config.checkField
        node.checkFieldType = config.checkFieldType
        node.log(JSON.stringify(config));
        node.on('input', function(msg) {
                const fractionsRegex = /^\d+(\.|\,)\d+$/;
                if(fractionsRegex.test(String(msg.topic))){
                    node.send([msg,])
                }else{
                    node.send([,msg])
                }
                
            
        });
    }
    RED.nodes.registerType("check_fractions",check_fractions_Node);
}