module.exports = function(RED) {
    function check_number_length_Node(config) {
        RED.nodes.createNode(this,config);
        var node = this;
        node.checkField = config.checkField
        node.checkFieldType = config.checkFieldType
        node.length = config.length
        //node.log(JSON.stringify(config));
        node.on('input', function(msg) {
                const numberLengthRegex = new RegExp('^\\d{'+Number(node.length)+'}$','i');
                if(numberLengthRegex.test(String(msg.topic))){
                    node.send([msg,])
                }else{
                    node.send([,msg])
                }
                
            
        });
    }
    RED.nodes.registerType("check_number_length",check_number_length_Node);
}