module.exports = function(RED) {
    function check_phone(config) {
        RED.nodes.createNode(this,config);
        var node = this;
        node.checkField = config.checkField
        node.checkFieldType = config.checkFieldType
        node.on('input', function(msg) {
                const re = /^(\+[1-9][0-9]*(\([0-9]*\)|-[0-9]*-))?[0]?[1-9][0-9\- ]*$/;
                if(re.test(String(msg.topic))){
                    node.send([msg,])
                }else{
                    node.send([,msg])
                }
                
            
        });
    }
    RED.nodes.registerType("check_phone",check_phone);
}
